import React, { Component } from 'react'
import axios from 'axios'




const baseUrl = 'http://localhost:3001/users'
const initialState = {
    user: { name: '', email: '', tell: '', cep: '', pais: '', UF: '', cidade: '', bairro: '', logradouro: '', numero: '' },
    list: []
}

export class CreateClients extends Component {

    state = { ...initialState }

    componentWillMount() {
        axios(baseUrl).then(resp => {
            this.setState({ list: resp.data })
        })
    }

    clear() {
        this.setState({ user: initialState.user })
    }

    save() {
        const user = this.state.user
        const method = user.id ? 'put' : 'post'
        const url = user.id ? `${baseUrl}/${user.id}` : baseUrl
        axios[method](url, user)
            .then(resp => {
                const list = this.getUpdatedList(resp.data)
                this.setState({ user: initialState.user, list })
            })
    }

    getUpdatedList(user, add = true) {
        const list = this.state.list.filter(u => u.id !== user.id)
        if(add) list.unshift(user)
        return list
    }

    updateField(event) {
        const user = { ...this.state.user }
        user[event.target.name] = event.target.value
        this.setState({ user })
    }

     renderForm() {
        return (
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Nome</label>
                            <input type="text" className="form-control"
                                name="name"
                                value={this.state.user.name}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o nome..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>E-mail</label>
                            <input type="text" className="form-control"
                                name="email"
                                value={this.state.user.email}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o e-mail..." />
                        </div>
                    </div>
                </div>

                <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>Contato</label>
                            <input type="text" className="form-control"
                                name="contato"
                                value={this.state.user.tell}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o seu Número..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>cep</label>
                            <input type="text" className="form-control"
                                name="cep"
                                value={this.state.user.cep}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o cep..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>País</label>
                            <input type="text" className="form-control"
                                name="email"
                                value={this.state.user.pais}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o seu País.." />
                        </div>
                    </div>

                    <div className="col-12 col-md-63">
                        <div className="form-group">
                            <label>Estado</label>
                            <input type="text" className="form-control"
                                name="UF"
                                value={this.state.user.UF}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o Seu estado..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>Cidade</label>
                            <input type="text" className="form-control"
                                name="cidade"
                                value={this.state.user.cidade}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite a sua cidade..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>Bairro</label>
                            <input type="text" className="form-control"
                                name="email"
                                value={this.state.user.bairro}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o  seu bairro..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>Logradouro</label>
                            <input type="text" className="form-control"
                                name="logradouro"
                                value={this.state.user.logradouro}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o seu logradouro..." />
                        </div>
                    </div>

                    <div className="col-12 col-md-3">
                        <div className="form-group">
                            <label>Número</label>
                            <input type="text" className="form-control"
                                name="email"
                                value={this.state.user.numero}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o número da sua residencia..." />
                        </div>
                    </div>

                <hr />
                <div className="row">
                    <div className="col-12 d-flex justify-content-end">
                        <button className="btn btn-primary"
                            onClick={e => this.save(e)}>
                            Salvar
                        </button>

                        <button className="btn btn-secondary ml-2"
                            onClick={e => this.clear(e)}>
                            Cancelar
                        </button>
                    </div>
                </div>
            </div>
        )

    }
    load(user) {
        this.setState({ user })
    }

    remove(user) {
        axios.delete(`${baseUrl}/${user.id}`).then(resp => {
            const list = this.getUpdatedList(user, false)
            this.setState({ list })
        })
    }
}
