import styled from 'styled-components'

export const AreaHeader = styled.div`
    height: 60px;
    background-image: linear-gradient(to right, #543a9c, #3e40b6);
    color: #fff;

    .container{
        padding: 5px 20px;
        display: flex;
        align-items: center;

    }

        .logo {
            flex: 1;
            font: 1.5rem "poppins", sans-serif;


        }
        nav {
            ul{
                align-items: center;
                display: flex;
            }

                li{
                    list-style: none;
                    margin-left: 20px;

                    a{
                        text-decoration:none;
                        color: #fff;
                    }
                }
        }

`;
